const React = require('react');
const { useEffect, useState } = React;
const detectEthereumProvider = require('@metamask/detect-provider');
const { ethers } = require('ethers');
const UnemploymentInsuranceAbi = require('../smart-contract/UnemploymentInsurance.json');

const CompanyRegistration = () => {
    const [userAddress, setUserAddress] = useState('');
    const [ethersProvider, setEthersProvider] = useState(null);
    const [roundContract, setRoundContract] = useState(null);
    const [companyName, setCompanyName] = useState('');
    const [hrWallet, setHrWallet] = useState('');
    const [status, setStatus] = useState('');

    useEffect(() => {
        const initializeWeb3 = async () => {
            try {
                const provider = await detectEthereumProvider();
                if (provider) {
                    console.log("MetaMask detected.");
                    await provider.request({ method: "eth_requestAccounts" });
                    const ethersProviderInstance = new ethers.providers.Web3Provider(provider);
                    const signer = ethersProviderInstance.getSigner();
                    const userAddress = await signer.getAddress();

                    setUserAddress(userAddress);
                    setEthersProvider(ethersProviderInstance);

                    const roundContractInstance = new ethers.Contract(
                        '0x4420cb0D64B112E7292F9D5C387Fd68b817F1709',
                        UnemploymentInsuranceAbi.abi,
                        signer
                    );
                    setRoundContract(roundContractInstance);
                } else {
                    console.error("MetaMask not detected.");
                }
            } catch (error) {
                console.error("Failed to connect to wallet:", error);
            }
        };

        initializeWeb3();
    }, []);

    const handleRegistration = async () => {
        try {
            setStatus('Waiting for transaction to be mined...');
            const tx = await roundContract.registerCompany(companyName, hrWallet);
            await tx.wait();
            setStatus('Company registered successfully!');
        } catch (error) {
            console.error('Error registering company:', error);
            setStatus('Error: Please check the console for details.');
        }
    };

    return (
        <div>
            <h2>Company Registration</h2>
            <div>
                <label htmlFor="companyName">Company Name:</label>
                <input
                    type="text"
                    id="companyName"
                    value={companyName}
                    onChange={(e) => setCompanyName(e.target.value)}
                />
            </div>
            <div>
                <label htmlFor="hrWallet">HR Wallet Address:</label>
                <input
                    type="text"
                    id="hrWallet"
                    value={hrWallet}
                    onChange={(e) => setHrWallet(e.target.value)}
                />
            </div>
            <button onClick={handleRegistration}>Register Company</button>
            <div>{status}</div>
            <div>User Address: {userAddress}</div>
        </div>
    );
};

module.exports = CompanyRegistration;
