import React, { useState, useEffect } from 'react';
import Web3 from 'web3';
import UnemploymentInsuranceABI from '../smart-contract/UnemploymentInsuranceABI.json';

function EmployeeRegistrationForm() {
  const [companyName, setCompanyName] = useState('');
  const [salary, setSalary] = useState('');
  const [employeeNameHash, setEmployeeNameHash] = useState('');
  const [emailAddress, setEmailAddress] = useState('');
  const [nric, setNric] = useState('');
  const [statusMessage, setStatusMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [userAccount, setUserAccount] = useState('');

  useEffect(() => {
    async function loadUserAccount() {
      if (window.ethereum) {
        const accounts = await window.ethereum.request({ method: 'eth_accounts' });
        setUserAccount(accounts[0]);
      }
    }

    loadUserAccount();
  }, []);

  const handleCompanyNameChange = (event) => {
    setCompanyName(event.target.value);
  };

  const handleSalaryChange = (event) => {
    setSalary(event.target.value);
  };

  const handleEmployeeNameHashChange = (event) => {
    setEmployeeNameHash(event.target.value);
  };

  const handleEmailAddressChange = (event) => {
    setEmailAddress(event.target.value);
  };

  const handleNricChange = (event) => {
    setNric(event.target.value);
  };

  const registerEmployee = async () => {
    try {
      setStatusMessage('');
      setLoading(true);

      if (window.ethereum) {
        await window.ethereum.enable();
      }

      const web3 = new Web3(window.ethereum || 'http://localhost:7545');
      const contractInstance = new web3.eth.Contract(
        UnemploymentInsuranceABI,
        '0x4420cb0d64b112e7292f9d5c387fd68b817f1709', // Replace with your contract address
      );

      const accounts = await web3.eth.getAccounts();
      const employeeAddress = accounts[0];

      await contractInstance.methods.registerEmployee(
        companyName,
        salary,
        employeeNameHash,
        emailAddress,
        nric,
      ).send({
        from: employeeAddress,
      });

      setStatusMessage('Employee registered successfully!');
    } catch (error) {
      console.error('Error:', error);
      setStatusMessage(`Error: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Register an Employee</h2>
      {userAccount && (
        <p>Connected Account: {userAccount}</p>
      )}
      <form onSubmit={(e) => { e.preventDefault(); registerEmployee(); }}>
        <label>
          Company Name:
          <input
            type="text"
            value={companyName}
            onChange={handleCompanyNameChange}
          />
        </label>
        <br />
        <label>
          Salary:
          <input
            type="number"
            value={salary}
            onChange={handleSalaryChange}
          />
        </label>
        <br />
        <label>
          Employee Name Hash:
          <input
            type="text"
            value={employeeNameHash}
            onChange={handleEmployeeNameHashChange}
          />
        </label>
        <br />
        <label>
          Email Address:
          <input
            type="email"
            value={emailAddress}
            onChange={handleEmailAddressChange}
          />
        </label>
        <br />
        <label>
          NRIC:
          <input
            type="text"
            value={nric}
            onChange={handleNricChange}
          />
        </label>
        <br />
        <button type="submit" disabled={loading}>
          {loading ? 'Loading...' : 'Register Employee'}
        </button>
      </form>
      <div>{statusMessage}</div>
    </div>
  );
}

export default EmployeeRegistrationForm;
