import React, { useState } from 'react';
import Web3 from 'web3';

const CompanyRegistration = ({ contract }) => {
  const [companyName, setCompanyName] = useState('');
  const [hrWallet, setHrWallet] = useState('');
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setSuccess(false);
    setError(null);

    try {
      const accounts = await window.ethereum.request({ method: 'eth_requestAccounts' });
      const senderAddress = accounts[0];

      await contract.methods
        .registerCompany(companyName, hrWallet)
        .send({ from: senderAddress });

      setSuccess(true);
      setCompanyName('');
      setHrWallet('');
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Register Company</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="companyName">Company Name:</label>
          <input
            type="text"
            id="companyName"
            value={companyName}
            onChange={(e) => setCompanyName(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="hrWallet">HR Wallet Address:</label>
          <input
            type="text"
            id="hrWallet"
            value={hrWallet}
            onChange={(e) => setHrWallet(e.target.value)}
            required
          />
        </div>
        <button type="submit" disabled={loading}>
          {loading ? 'Registering...' : 'Register Company'}
        </button>
      </form>
      {success && <p>Company registered successfully!</p>}
      {error && <p>Error: {error}</p>}
    </div>
  );
};

export default CompanyRegistration;
