import React, { useEffect, useState } from 'react';
import Web3 from 'web3';
import UnemploymentInsuranceContract from '../smart-contract/UnemploymentInsurance.json'; // Import the compiled contract ABI

const CompanyRegistration = () => {
    const [userAddress, setUserAddress] = useState('');
    const [web3, setWeb3] = useState(null);
    const [roundContract, setRoundContract] = useState(null);
    const [companyName, setCompanyName] = useState('');
    const [hrWallet, setHrWallet] = useState('');
    const [status, setStatus] = useState('');

    useEffect(() => {
        const initializeWeb3 = async () => {
            try {
                // Check if MetaMask is installed and connected
                if (window.ethereum) {
                    console.log("MetaMask detected.");
                    await window.ethereum.request({ method: "eth_requestAccounts" });
                    const web3Instance = new Web3(window.ethereum);

                    const accounts = await web3Instance.eth.getAccounts();
                    setUserAddress(accounts[0]);
                    setWeb3(web3Instance);

                    // Hardcode the network ID for Ganache (replace <<GANACHE_NETWORK_ID>> with your Ganache network ID)
                    const deployedNetwork = UnemploymentInsuranceContract.networks['1337'];
                    const roundContractInstance = new web3Instance.eth.Contract(
                        UnemploymentInsuranceContract.abi,
                        deployedNetwork && deployedNetwork.address
                    );
                    setRoundContract(roundContractInstance);
                } else {
                    console.error("MetaMask not detected. Please install MetaMask to interact with this application.");
                }
            } catch (error) {
                console.error("Failed to connect to MetaMask:", error);
            }
        };

        initializeWeb3();
    }, []);

    const handleRegistration = async () => {
        try {
            setStatus('Waiting for transaction to be mined...');
            await roundContract.methods.registerCompany(companyName, hrWallet).send({ from: userAddress });
            setStatus('Company registered successfully!');
        } catch (error) {
            console.error('Error registering company:', error);
            setStatus('Error: Please check the console for details.');
        }
    };

    return (
        <div>
            <h2>Company Registration</h2>
            <div>
                <label htmlFor="companyName">Company Name:</label>
                <input
                    type="text"
                    id="companyName"
                    value={companyName}
                    onChange={(e) => setCompanyName(e.target.value)}
                />
            </div>
            <div>
                <label htmlFor="hrWallet">HR Wallet Address:</label>
                <input
                    type="text"
                    id="hrWallet"
                    value={hrWallet}
                    onChange={(e) => setHrWallet(e.target.value)}
                />
            </div>
            <button onClick={handleRegistration}>Register Company</button>
            <div>{status}</div>
            <div>User Address: {userAddress}</div>
        </div>
    );
};

export default CompanyRegistration;
