import React, { useState } from 'react';
import Web3 from 'web3';
import UnemploymentInsuranceContract from '../smart-contract/UnemploymentInsurance.json'; // Import the compiled contract ABI

const CompanyRegistration = () => {
    const [companyName, setCompanyName] = useState('');
    const [hrWallet, setHrWallet] = useState('');
    const [status, setStatus] = useState('');

    const handleRegistration = async () => {
        try {
            setStatus('Waiting for transaction to be mined...');
            console.log("start")
            const web3 = new Web3(Web3.givenProvider || 'http://localhost:8545'); // Initialize Web3
            const networkId = await web3.eth.net.getId();
            const deployedNetwork = UnemploymentInsuranceContract.networks[networkId];
            const contractInstance = new web3.eth.Contract(
                UnemploymentInsuranceContract.abi,
                deployedNetwork && deployedNetwork.address
            );

            const accounts = await web3.eth.getAccounts();
            const ownerAccount = accounts[0]; // Assuming the owner's account is the first account in the array

            await contractInstance.methods.registerCompany(companyName, hrWallet).send({ from: ownerAccount });

            setStatus('Company registered successfully!');
        } catch (error) {
            console.error('Error registering company:', error);
            setStatus('Error: Please check the console for details.');
        }
    };

    return (
        <div>
            <h2>Company Registration</h2>
            <div>
                <label htmlFor="companyName">Company Name:</label>
                <input
                    type="text"
                    id="companyName"
                    value={companyName}
                    onChange={(e) => setCompanyName(e.target.value)}
                />
            </div>
            <div>
                <label htmlFor="hrWallet">HR Wallet Address:</label>
                <input
                    type="text"
                    id="hrWallet"
                    value={hrWallet}
                    onChange={(e) => setHrWallet(e.target.value)}
                />
            </div>
            <button onClick={handleRegistration}>Register Company</button>
            <div>{status}</div>
        </div>
    );
};

export default CompanyRegistration;
