import React, { useState } from 'react';
import Web3 from 'web3';
import UnemploymentInsuranceContract from './contracts/UnemploymentInsurance.json';

function CompanyRegistrationForm() {
  const [companyName, setCompanyName] = useState('');
  const [hrWalletAddress, setHrWalletAddress] = useState('');
  const [statusMessage, setStatusMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const handleCompanyNameChange = (event) => {
    setCompanyName(event.target.value);
  };

  const handleHrWalletAddressChange = (event) => {
    setHrWalletAddress(event.target.value);
  };

  const registerCompany = async () => {
    try {
      setStatusMessage('');
      setLoading(true);

      const web3 = new Web3(Web3.givenProvider || 'http://localhost:7545');
      const networkId = await web3.eth.net.getId();
      const deployedNetwork = UnemploymentInsuranceContract.networks[networkId];
      const contractInstance = new web3.eth.Contract(
        UnemploymentInsuranceContract.abi,
        deployedNetwork && deployedNetwork.address,
      );

      // Assuming the user is already authenticated via MetaMask
      const accounts = await web3.eth.getAccounts();
      const ownerAddress = accounts[0];

      await contractInstance.methods.registerCompany(companyName, hrWalletAddress).send({
        from: ownerAddress,
      });

      setStatusMessage('Company registered successfully!');
    } catch (error) {
      setStatusMessage(`Error: ${error.message}`);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Register a Company</h2>
      <form onSubmit={(e) => { e.preventDefault(); registerCompany(); }}>
        <label>
          Company Name:
          <input
            type="text"
            value={companyName}
            onChange={handleCompanyNameChange}
          />
        </label>
        <br />
        <label>
          HR Wallet Address:
          <input
            type="text"
            value={hrWalletAddress}
            onChange={handleHrWalletAddressChange}
          />
        </label>
        <br />
        <button type="submit" disabled={loading}>
          {loading ? 'Loading...' : 'Register Company'}
        </button>
      </form>
      <div>{statusMessage}</div>
    </div>
  );
}

export default CompanyRegistrationForm;
